import React, { useState } from 'react';
import axios from 'axios'
import './App.css';
import './tailwind_styles.css'
import 'antd/dist/antd.css';

import {Upload, Button, Icon} from 'antd'

function App() {

  const [selectedFile, setSelectedFile] = useState()
  const [uploadedFile, setUploadedFile] = useState()

  function handleFileSelect (info) {
    console.log(info.file)
    //setSelectedFile(event.target.files[0])
  }

  function handleFileUpload (event) {
    const fd = new FormData ()

    fd.append('image', selectedFile)
    axios.post('http://localhost:5000/upload_file', fd, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    .then(res => {
      const {fileName, filePath} = res.data 
      setUploadedFile({fileName, filePath})
    })
    .catch(error => console.log(error))
    console.log(selectedFile)

  }

  return (
    <>
      <h1 className="text-4xl self-center font-bold bg-blue-200 py-2 pl-64">ScaleRes.</h1>

      <div className="px-64 pt-10">

        <p className="text-base">Enter a blurry image you'd like to scale up. Our MegaSuperAdvancedâ„¢ 
        Algorithms will then increase the resolution of your image using [insert buzzword here]</p>

      <Upload onChange={handleFileSelect}>
        <Button>
          <Icon type="upload" /> Click to Upload
        </Button>
      </Upload>

      <Button type="primary" className="mt-3">Upload</Button>
      </div>
    </>
  );
}

export default App
