import React, { useState } from 'react';
import axios from 'axios'
import './App.css';
import './tailwind_styles.css'
import 'antd/dist/antd.css';

import {Upload, Button, Icon} from 'antd'

function App() {

  const [selectedFile, setSelectedFile] = useState()

  function handleFileSelect (event) {
    setSelectedFile(event.target.files[0])
  }

  function handleFileUpload (event) {
    const fd = new FormData
    fd.append('image', selectedFile, selectedFile.name)
    /*axios.post('http://localhost:5000/upload_file', fd)
    .then(res => console.log(res))*/
    console.log(fd)

  }

  return (
    <>
      <h1 className="text-4xl self-center font-bold bg-blue-200 py-2 pl-64">ScaleRes.</h1>

      <div className="px-64 pt-10">

        <p className="text-base">Enter a blurry image you'd like to scale up. Our MegaSuperAdvancedâ„¢ 
        Algorithms will then increase the resolution of your image using [insert buzzword here]</p>

        {/*<Upload onChange={handleFileUpload}>
          <Button>
            <Icon type="upload" /> Upload
          </Button>
        </Upload>*/}

        <input type="file" onChange={handleFileSelect}></input><br/>
        <button className="mt-4" onClick={handleFileUpload}>Upload</button>
      </div>
    </>
  );
}

export default App;
